		public void 切换PK模式(int PK)
		{
			byte[] array = Converter.hexStringToByte("AA550800930416100200020055AA");
			System.Buffer.BlockCopy(BitConverter.GetBytes(PK), 0, array, 10, 1);
			base.人物PK模式 = PK;
			心跳次数阀值 = 3;
			if (base.Client != null)
			{
				base.Client.Send(array, array.Length);
			}
		}

