		public bool 得到任务物品(int 物品ID, int 物品数量)
		{
			int num = 0;
			while (true)
			{
				if (num < 任务物品.Length)
				{
					if (任务物品[num].物品ID == 物品ID && 任务物品[num].物品数量 >= 物品数量)
					{
						break;
					}
					num++;
					continue;
				}
				return false;
			}
			return true;
		}

		public void 设置任务物品(int 物品ID, int 物品数量)
		{
			bool flag = false;
			bool flag2 = false;
			for (int i = 0; i < 任务物品.Length; i++)
			{
				if (任务物品[i].物品ID == 物品ID)
				{
					flag = true;
					flag2 = true;
					if (物品数量 == 0)
					{
						得到任务物品(i, 任务物品[i].物品ID, 0);
						任务物品[i].物品_byte = new byte[8];
					}
					else
					{
						任务物品[i].物品数量 += 物品数量;
						得到任务物品(i, 任务物品[i].物品ID, 任务物品[i].物品数量);
					}
					break;
				}
			}
			if (!flag && 物品数量 > 0)
			{
				for (int j = 0; j < 任务物品.Length; j++)
				{
					if (任务物品[j].物品ID == 0)
					{
						任务物品[j].物品ID = 物品ID;
						任务物品[j].物品数量 += 物品数量;
						得到任务物品(j, 任务物品[j].物品ID, 任务物品[j].物品数量);
						flag2 = true;
						break;
					}
				}
			}
			if (!flag2)
			{
				系统提示("任务物品已满不能继续。");
			}
		}

