		public double 计算宠物追加经验百分比(int 忠诚度)
		{
			if (忠诚度 >= 0 && 忠诚度 <= 200)
			{
				return -0.8;
			}
			if (忠诚度 > 200 && 忠诚度 <= 500)
			{
				return -0.5;
			}
			if (忠诚度 > 500 && 忠诚度 <= 800)
			{
				return -0.2;
			}
			if (忠诚度 > 800 && 忠诚度 <= 1000)
			{
				return -0.1;
			}
			if (忠诚度 > 1000 && 忠诚度 <= 1400)
			{
				return 0.0;
			}
			if (忠诚度 > 1400 && 忠诚度 <= 1700)
			{
				return 0.03;
			}
			if (忠诚度 > 1700 && 忠诚度 <= 1900)
			{
				return 0.04;
			}
			if (忠诚度 > 1900 && 忠诚度 <= 2000)
			{
				return 0.05;
			}
			return 0.0;
		}

